-- Create boss_spawns table to track boss spawn times
CREATE TABLE IF NOT EXISTS boss_spawns (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  spawned_at bigint NOT NULL,
  created_at timestamp with time zone DEFAULT now(),
  updated_at timestamp with time zone DEFAULT now()
);

-- Enable RLS
ALTER TABLE boss_spawns ENABLE ROW LEVEL SECURITY;

-- Allow anyone to read boss spawn times (public data)
CREATE POLICY "Anyone can view boss spawns" 
  ON boss_spawns 
  FOR SELECT 
  USING (true);

-- Allow anyone to insert boss spawn times (any user can report a spawn)
CREATE POLICY "Anyone can insert boss spawns" 
  ON boss_spawns 
  FOR INSERT 
  WITH CHECK (true);

-- Create index for performance
CREATE INDEX idx_boss_spawns_spawned_at ON boss_spawns(spawned_at DESC);

-- Enable realtime for this table
ALTER PUBLICATION supabase_realtime ADD TABLE boss_spawns;

-- Create function to broadcast boss spawn events
CREATE OR REPLACE FUNCTION broadcast_boss_spawn()
RETURNS TRIGGER AS $$
BEGIN
  PERFORM realtime.send(
    'boss:spawns',
    'boss_spawned',
    jsonb_build_object(
      'id', NEW.id,
      'spawned_at', NEW.spawned_at,
      'created_at', NEW.created_at
    ),
    false
  );
  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Create trigger to broadcast on insert
CREATE TRIGGER boss_spawn_broadcast_trigger
  AFTER INSERT ON boss_spawns
  FOR EACH ROW
  EXECUTE FUNCTION broadcast_boss_spawn();

SELECT pg_get_functiondef(oid) as function_definition
FROM pg_proc
WHERE proname = 'broadcast_boss_spawn';

SELECT schemaname, tablename 
FROM pg_tables 
WHERE schemaname = 'public' AND tablename = 'boss_spawns';

-- Drop the old trigger and function
DROP TRIGGER IF EXISTS boss_spawn_broadcast_trigger ON boss_spawns;
DROP FUNCTION IF EXISTS broadcast_boss_spawn();

-- Create new trigger function using realtime.broadcast_changes
CREATE OR REPLACE FUNCTION broadcast_boss_spawn()
RETURNS TRIGGER AS $$
SECURITY DEFINER
LANGUAGE plpgsql
AS $$
BEGIN
  -- Use realtime.broadcast_changes for better scalability
  PERFORM realtime.broadcast_changes(
    'boss:spawns',
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN NEW;
END;
$$;

-- Create trigger
CREATE TRIGGER boss_spawn_broadcast_trigger
  AFTER INSERT OR UPDATE OR DELETE ON boss_spawns
  FOR EACH ROW EXECUTE FUNCTION broadcast_boss_spawn();

-- Ensure RLS is enabled
ALTER TABLE boss_spawns ENABLE ROW LEVEL SECURITY;

-- Update RLS policies to allow public access (since this is a public notification system)
DROP POLICY IF EXISTS "Anyone can view boss spawns" ON boss_spawns;
DROP POLICY IF EXISTS "Anyone can insert boss spawns" ON boss_spawns;

CREATE POLICY "Anyone can view boss spawns" ON boss_spawns
  FOR SELECT TO public USING (true);

CREATE POLICY "Anyone can insert boss spawns" ON boss_spawns
  FOR INSERT TO public WITH CHECK (true);

-- Drop the old trigger and function
DROP TRIGGER IF EXISTS boss_spawn_broadcast_trigger ON boss_spawns;
DROP FUNCTION IF EXISTS broadcast_boss_spawn();

-- Create new trigger function using realtime.broadcast_changes
CREATE OR REPLACE FUNCTION broadcast_boss_spawn()
RETURNS TRIGGER
SECURITY DEFINER
LANGUAGE plpgsql
AS $$
BEGIN
  -- Use realtime.broadcast_changes for better scalability
  PERFORM realtime.broadcast_changes(
    'boss:spawns',
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN NEW;
END;
$$;

-- Create trigger
CREATE TRIGGER boss_spawn_broadcast_trigger
  AFTER INSERT OR UPDATE OR DELETE ON boss_spawns
  FOR EACH ROW EXECUTE FUNCTION broadcast_boss_spawn();

-- Ensure RLS is enabled
ALTER TABLE boss_spawns ENABLE ROW LEVEL SECURITY;

-- Update RLS policies to allow public access (since this is a public notification system)
DROP POLICY IF EXISTS "Anyone can view boss spawns" ON boss_spawns;
DROP POLICY IF EXISTS "Anyone can insert boss spawns" ON boss_spawns;

CREATE POLICY "Anyone can view boss spawns" ON boss_spawns
  FOR SELECT TO public USING (true);

CREATE POLICY "Anyone can insert boss spawns" ON boss_spawns
  FOR INSERT TO public WITH CHECK (true);

-- Enable RLS on realtime.messages if not already enabled
ALTER TABLE realtime.messages ENABLE ROW LEVEL SECURITY;

-- Allow anyone to read boss spawn broadcasts
CREATE POLICY "Anyone can read boss spawn broadcasts" ON realtime.messages
  FOR SELECT TO public
  USING (topic = 'boss:spawns');

-- Allow anyone to send boss spawn broadcasts (needed for the trigger)
CREATE POLICY "Anyone can send boss spawn broadcasts" ON realtime.messages
  FOR INSERT TO public
  WITH CHECK (topic = 'boss:spawns');
